mapper.select:
SELECT d.*, c.name as categoryName FROM dish d LEFT JOIN category c
ON d.category_id = c.id


        AND d.name LIKE CONCAT('%', #{name}, '%')


        AND d.status = #{status}


        AND d.category_id = #{categoryId}


ORDER BY create_time DESC

SELECT * FROM dish d


        AND d.name LIKE CONCAT('%', #{name}, '%')


        AND d.status = #{status}


        AND d.category_id = #{categoryId}
